import React from 'react'

const Burger = ({burger,cart,setCart,burgers}) => {

const {nombre,precio,id} = burger;

//Filtro por id la burger, y agrega debajo si hay alguna cargada
const addBurger = id =>{
  const burger = burgers.filter((burger) => burger.id === id);

  setCart([...cart,...burger])
}

const delBurger = (id) => {
  const burgers = cart.filter((burger)=> burger.id === id);

  setCart([...cart, ...burger]);
};



  return (
    <div>
      <ul>
        <li>{nombre}</li>
        <li>${precio}</li>

        {burgers ? (
          <button type="button" onClick={() => addBurger(id)}>
            Agregar al carrito
          </button>
        ) : (
          <button type="button" onClick={() => addBurger(id)}>
            Eliminar del carrito
          </button>
        )}

        <button type="button" onClick={() => addBurger(id)}>
          Agregar al carrito
        </button>
      </ul>
    </div>
  );
}

export default Burger
