import React,{ Fragment , useState } from "react";
import Burger from "./components/Burger";

function App() {

  const [burgers, setBurger] = useState([
    { id: 1, nombre: "Burger Completa", precio: 200 },
    { id: 2, nombre: "Burger Jamon", precio: 200 },
    { id: 3, nombre: "Burger Jamon y queso" },
    { id: 4, nombre: "Burger Tomate y Huevo" },
  ]);

  //Estado del carrito

  const [cart,setCart] = useState([])

  return (
    <Fragment>
      <h3>Burgers</h3>
      {burgers.map((burger) =>(
        <Burger
          key={burger.id}
          burger={burger}
          cart={cart}
          setCart = {setCart}
          burgers = {burgers}
        />
      ))}

    </Fragment>
  );
}

export default App;
