import React from 'react'
import Burger from './Burger';

export const Cart = ({ cart, setCart }) => {
  return (
    <div>
       <h3>Carrito</h3>

        {cart.length === 0 
        ? (<p>0 </p>)
         : 
         (cart.map((burger) => <Burger key={burger.id}
          burger={burger} 
          cart={cart}
           setCart={setCart}
        />)) } 


    </div>
  );
}
