import React,{ Fragment , useState } from "react";
import Burger from "./components/Burger";
import Cart from "./components/Cart";

function App() {

  const [burgers, setBurger] = useState([
    { id: 1, nombre: "Burger Completa", precio: 300 },
    { id: 2, nombre: "Burger Jamon", precio: 250 },
    { id: 3, nombre: "Burger Jamon y queso", precio: 270 },
    { id: 4, nombre: "Burger Tomate y Huevo", precio: 250 },
  ]);

  //Estado del carrito

  const [cart,setCart] = useState([ ]);

  return (
    <Fragment>
      <h3>Burgers</h3>
      {burgers.map((burger) =>(
        <Burger
          key={burger.id}
          burger={burger}
          cart={cart}
          setCart = {setCart}
          burgers = {burgers}
        />
      ))}

      <Cart cart={cart} setCart={setCart}/>
    </Fragment>
  );
}

export default App;
